mapper.update:
update
seckill
set
numbera = numbera -1
where
seckill_id = #{seckillId}
and
start_time  <= 
#{killTime}
and end_time  >= 
#{killTime}
and numbera > 0;

update
seckill
set
end_time = #{endTime}
where
seckill_id = #{seckillId}
and numbera > 0;

mapper.select:
call execute_seckill(
	#{seckillId, jdbcType=BIGINT, mode=IN},
	#{phone, jdbcType=BIGINT, mode=IN},
	#{killTime, jdbcType=TIMESTAMP, mode=IN},
	#{result, jdbcType=INTEGER, mode=OUT}
)
